var autoplay = false
      

    var autoplay_video = document.createElement('video');
    var mp4 = 'data:video/mp4;base64,';
    var webm = 'data:video/webm;base64,';

    autoplay_video.src = autoplay_video.canPlayType('video/webm;codecs="vp8"') ? webm : mp4;

  //set attributes -needs to be visible or IE squawks, so we move it way outside  
    autoplay_video.id = "base64_test_video";
    autoplay_video.autoplay = true;
    autoplay_video.style.position = "fixed";
    autoplay_video.style.left = "5000px";
  
  //add to DOM       
    document.getElementsByTagName("body")[0].appendChild(autoplay_video);
   
  //test for autoplay, 100 ms buffer   
    setTimeout(function(){
        var base64_test_video = document.getElementById("base64_test_video");
        if(!base64_test_video.paused){
            autoplay = true;
        } else {
        }
        console.log('ici, autoplay = ' + autoplay)
        document.getElementsByTagName("body")[0].removeChild(autoplay_video);
    
    },100);

